// Get raw AI text (OpenAI / Gemini compatible via n8n)
const raw = $json.output?.[0]?.content?.[0]?.text;

if (!raw) {
  throw new Error("AI output text not found");
}

// Clean markdown / code fences if present
const cleaned = raw
  .replace(/```json/g, "")
  .replace(/```/g, "")
  .trim();

// Parse JSON string into object
const parsed = JSON.parse(cleaned);

// Return structured fields + keep Email for Google Sheet update
return {
  ...parsed,
  Email: $json.Email,
};
